package ConditionalStatementsNew;

public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=3;
		
		switch(x)
		{
		case 1:
		System.out.println("Poor Performer");
		break;
		
		case 2:
			System.out.println("Good Performer");
		break;
		
		case 3:
			System.out.println("Excellent Performer");
		break;
		
		case 4:
			System.out.println("Employee of the Year");
		break;
		
		case 5:
			System.out.println("Out of the Box");
		break;
			
			default:
				System.out.println("You are fired");
		
		}
		

	}

}
