package Java;

public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int ageA =20;
		int ageB =25;
		
		//Equal To Operator
		System.out.println(ageA==ageB);
		
		//NotEqual To
		System.out.println(ageA!=ageB);
		
		//Greater Than
		
		System.out.println(ageA>ageB);
		
		//Less than
		System.out.println(ageA<ageB);
		
		//Greater Than or equal to
		System.out.println(ageA<=ageB);
		
		//Less Than or equal to
		System.out.println(ageA>=ageB);
	}

}
