package JavaPrograms;

public class OddorEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=21;
		
		if(number % 2 == 0) {
			System.out.println("Given Number is Even");
		}
		else {
			System.out.println("Given Number is Odd");
		}

	}

}
