package JavaPrograms;

public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=100; int y=200;
		
		int temp = x;
		
		x=y;
		
		y = temp;
		
		System.out.println("Value of X:"+ x  + " Value of Y:"+y);
		
		

	}

}
