package Operators;

public class AssignmnetOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=100;
		int num2=50;
		
		//num1=num2 - Assigning value of variable 2 to variable 1
		
		num1=num2;
		System.out.println("Output of num1=num2 is:"+num1);
		
		//num1=num1+num2
		num1+=num2;
		System.out.println("Output of num1=num1+num2 is:"+num1);

		
		//num1=num1-num2
				num1-=num2;
				System.out.println("Output of num1=num1-num2 is:"+num1);
				
			//num1=num1-num2
			num1*=num2;
			System.out.println("Output of num1=num1*num2 is:"+num1);	
			
			num1++;
			System.out.println("Incremental value of num1:"+num1);
				
				
				
	}

}
